import math

class Solution:
    def numPrimeArrangements(self, n: int) -> int:
        def isPrime(x):
            if x < 2:
                return False
            for i in range(2, int(x ** 0.5) + 1):
                if x % i == 0:
                    return False
            return True

        num_primes = sum(isPrime(i) for i in range(1, n + 1))
        remainder = pow(10, 9) + 7
        return (math.factorial(num_primes) * math.factorial(n - num_primes)) % remainder

