from typing import List
from collections import Counter
from math import gcd

class Solution:
    def hasGroupsSizeX(self, deck: List[int]) -> bool:
        count = Counter(deck)
        g = 0  # gcd
        for i in count.values():
            g = gcd(g, i)
            if g == 1:
                return False
        return True

