from typing import List

class Solution:
    def surfaceArea(self, grid: List[List[int]]) -> int:
        N = len(grid)
        area = 0

        for r in range(N):
            for c in range(N):
                if grid[r][c]: area += 2 + grid[r][c]*4
                if r: area -= min(grid[r][c], grid[r-1][c])*2
                if c: area -= min(grid[r][c], grid[r][c-1])*2

        return area

