class Solution:

  def maximumScore(self, nums, k):
    MOD = 10**9 + 7
    n = len(nums)

    def prime_score(num):
      i, count, factors = 2, 0, set()
      while i * i <= num:
        if num % i:
          i += 1
        else:
          num //= i
          factors.add(i)
      if num > 1:
        factors.add(num)
      return len(factors)

    prime_scores = [prime_score(num) for num in nums]
    prime_max_pos = [i for i in range(n)]
    for i in range(n - 2, -1, -1):
      if prime_scores[i] >= prime_scores[prime_max_pos[i + 1]]:
        prime_max_pos[i] = i
      else:
        prime_max_pos[i] = prime_max_pos[i + 1]

    dp = [0] * n
    for _ in range(k):
      new_dp = [0] * n
      for r in range(n):
        l = prime_max_pos[r]
        score = dp[l - 1] if l else 0
        score = max(score, dp[r - 1] if r else 0)
        score = (score + nums[l] * (dp[l - 2] if l - 1 >= 0 else 1)) % MOD
        new_dp[r] = score
      dp = new_dp

    return max(dp)

