    def cycleLengthQueries(self, n: int, queries: [[int]]) -> [int]:
        def parent(node):
            return node >> 1 

        def dist(node1, node2):
            path1, path2 = [], []

            while node1:
                path1.append(node1)
                node1 = parent(node1)

            while node2:
                path2.append(node2)
                node2 = parent(node2)

            i1, i2 = len(path1) - 1, len(path2) - 1
            while i1 >= 0 and i2 >= 0 and path1[i1] == path2[i2]:
                i1, i2 = i1 - 1, i2 - 1 

            return len(path1) + len(path2) - 2 *(i1 + 1)

        return [dist(*query) for query in queries]

