from typing import List
from math import ceil

class Solution:
    def makeSimilar(self, nums: List[int], target: List[int]) -> int:
        differences = [nums[i] - target[i] for i in range(len(nums))]
        positives = [d for d in differences if d > 0]
        negatives = [d for d in differences if d < 0]
        return sum(abs(sum(positives) // 4) + sum(abs(sum(negatives) // 4)) + ceil(abs(sum(positives) % 4) / 2) + ceil(abs(sum(negatives) % 4) / 2))

