from typing import List

class Solution:
    def buildMatrix(self, k: int, rowConditions: List[List[int]], colConditions: List[List[int]]) -> List[List[int]]:
        from collections import defaultdict, Counter

        def topological_sort(edges: List[List[int]], num_nodes: int) -> List[int]:
            adjacent = defaultdict(list)
            in_degree = [0]*(num_nodes+1)

            for a, b in edges:
                adjacent[a].append(b)
                in_degree[b] += 1

            queue = []
            for i in range(1, num_nodes+1):
                if in_degree[i] == 0:
                    queue.append(i)

            result = []
            while len(queue) != 0:
                node = queue.pop()
                for neighbour in adjacent[node]:
                    in_degree[neighbour] -= 1
                    if in_degree[neighbour] == 0:
                        queue.append(neighbour)
                result.append(node)

            if len(result) < num_nodes:
                return [] # Not possible to satisfy all conditions (there is a cycle)
            return result[::-1] # Reverse the result because the elements were appended at the end

        # Find correct order for rows and columns
        row_order = topological_sort(rowConditions, k)
        col_order = topological_sort(colConditions, k)
        if not row_order or not col_order:
            return []

        # Initialize the matrix and fill it with the numbers following the order for rows and columns obtained
        matrix = [[0]*k for _ in range(k)]
        for num in range(1, k+1):
            matrix[row_order.index(num)][col_order.index(num)] = num
        for row in matrix:
            counter = Counter(row)
            for i in range(1, k+1):
                if counter[i] != 1:
                    return []

        return matrix

