class Solution:
    def minimumLengthTrio(self, a: str, b: str, c: str) -> str:

        for s in sorted([a, b, c], key=len):
            for t in sorted([a, b, c], key=len):
                if s != t and is_subsequence(s, t):
                    u, v = sorted([s, t], key=len)
                    if all(is_subsequence(u + v[len(u):], w) for w in [a, b, c]):
                        return u + v[len(u):]

        res = a + b[len(a):] + c[len(b):]
        for s1 in [a, b, c]:
            for s2 in [a, b, c]:
                if s1 != s2:
                    for s3 in [a, b, c]:
                        if s3 not in [s1, s2]:
                            t1, t2, t3 = sorted([s1, s2, s3], key=len)
                            tmp = t1 + t2[len(t1):] + t3[len(t2):]
                            if len(tmp) < len(res):
                                res = tmp
        return res

