class Solution:
    def add_minimum(self, word: str) -> int:
        n = len(word)
        dp = [[[0]*51 for _ in range(51)] for _ in range(51)]
        next_op = [[[0]*51 for _ in range(51)] for _ in range(51)]

        for i in range(n-1, -1, -1):
            for ca in range(51):
                for cb in range(51):
                    if ca+cb > n-i:
                        continue
                    cc = n-i-ca-cb
                    if ca > cb or cb > cc:
                        continue
                    takeA = dp[i+1][ca+1][cb] + (word[i] != 'a')
                    takeB = dp[i+1][ca][cb+1] + (word[i] != 'b') if ca > cb else float('inf')
                    takeC = dp[i+1][ca][cb] + (word[i] != 'c') if cb > cc else float('inf')  
                    dp[i][ca][cb] = min(takeA, takeB, takeC)
                    if takeA == dp[i][ca][cb]:
                        next_op[i][ca][cb] = 'a'
                    elif takeB == dp[i][ca][cb]:
                        next_op[i][ca][cb] = 'b'
                    else:
                        next_op[i][ca][cb] = 'c'
        return dp[0][0][0]

