from heapq import heapify, heappush, heappop

class Solution:
  def robotWithString(self, s: str) -> str:
    pq = []
    heapify(pq)
    t, p = '', ''
    for c in s:
      heappush(pq, c)
      t += c
      while pq and pq[0] == t[-1]:
        heappop(pq)
        p, t = p + t[-1], t[:-1]
    return p + t[::-1]

