from typing import List

class FoodRatings:

    def __init__(self, foods: List[str], cuisines: List[str], ratings: List[int]):
        self.food_ratings = {food: rating for food, rating in zip(foods, ratings)}
        self.cuisine_food = {cuisine: [] for cuisine in cuisines}
        for food, cuisine in zip(foods, cuisines):
            self.cuisine_food[cuisine].append(food)


    def changeRating(self, food: str, newRating: int) -> None:
        self.food_ratings[food] = newRating        

    def highestRated(self, cuisine: str) -> str:
        highest_rat_food = sorted(self.cuisine_food[cuisine], key=lambda x: (-self.food_ratings[x], x))[0]
        return highest_rat_food
