class Solution:
    def distMoney(self, money: int, children: int) -> int:
        if money < children:  # not enough to give each child 1 dollar
            return -1

        money -= children  # subtract 1 dollar given to each child from total money
        eights, money = divmod(money, 8)  # calculate how many children can get 8 dollars

        while eights > 0:
            fives, money = divmod(money, 5)  # calculate how many children can get 5 dollars
            threes, money = divmod(money, 3)  # calculate how many children can get 3 dollars
            ones, money = divmod(money, 1)  # calculate how many children can get 1 dollar

            if money == 0:
                return eights
            else:
                eights -= 1  # remove one child from 8 dollars receivers
                money += 8  # add back the 8 dollars to the money

        return -1

