class Solution:
    def findMinimumTime(self, tasks):
        tasks.sort(key=lambda x: (x[1], -x[2]))
        pq = []
        run_sum = 0    
        for start, end, duration in tasks:
            if end >= run_sum + duration:
                heapq.heappush(pq, -duration)
                run_sum += duration
            elif pq and -pq[0] > duration:
                run_sum += duration - -heapq.heapreplace(pq, -duration)
        return run_sum

