completion:
class Solution:
    def minimumCost(self, s: str) -> int:
        cost_1 = s.count('1')
        cost_0 = s.count('0')

        curr_cost_1 = 0
        curr_cost_0 = 0

        min_cost = min(cost_1, cost_0)

        for i in range(len(s) - 1):
            if s[i] == '0':
                curr_cost_0 += 1
            else:
                curr_cost_1 += 1

            temp_cost = min(curr_cost_0 + cost_1 - curr_cost_1, curr_cost_1 + cost_0 - curr_cost_0)

            min_cost = min(min_cost, temp_cost)

        return min_cost

