class Solution:
    def maxPower(self, stations, r, k):
        prefix_sum = [0] * (len(stations) + 2 * r + 2)
        stations = [0] * r + stations
        total_power = [0] * len(stations)

        for i in range(r, len(stations)):
            prefix_sum[i+1] = prefix_sum[i] + stations[i]

        for _ in range(k):
            for i in range(r, len(stations)):
                total_power[i] = prefix_sum[min(i + r + 1, len(stations))] - prefix_sum[i - r]
            min_power_city = total_power.index(min(total_power))
            stations[min_power_city] += 1
            for i in range(min_power_city, len(stations)):
                prefix_sum[i+1] = prefix_sum[i] + stations[i]

        return min(total_power)

