class Solution:
    def isItPossible(self, word1: str, word2: str) -> bool:
        unique1 = len(set(word1))
        unique2 = len(set(word2))
        intersection = set(word1) & set(word2)
        if unique1 == unique2:
            return True
        if intersection:
            if abs(unique1 - unique2) > 1:
                return False
            else:
                if unique1 > unique2 and any(c not in intersection for c in word1):
                    return True
                elif unique2 > unique1 and any(c not in intersection for c in word2):
                    return True
        else:
            return True
        return False

