import numpy as np

class Solution:
    def closestPrimes(self, left: int, right: int) -> list[int]:
        # Function to check if a number is prime
        def is_prime(n):
            if n==2 or n==3: return True
            if n%2==0 or n==1: return False
            for i in range(3,int(n**0.5)+1,2):
                if n%i==0:
                    return False

            return True

        # Initializing minimum difference and pair
        min_diff = right - left
        min_pair = [-1, -1]

        # Track previous prime
        prev_prime = -1
        for num in range(left, right+1):
            # If number is prime
            if is_prime(num):
                # If this is not the first prime we've encountered
                if prev_prime != -1:
                    # If this difference is less than minimum difference found so far
                    if num - prev_prime < min_diff:
                        min_diff = num - prev_prime
                        min_pair = [prev_prime, num]
                # Update previous prime
                prev_prime = num

        return min_pair

