MOD = 10**9 + 7

# Precompute factorials upto n
factorials = [0]*100001
factorials[0] = 1
for i in range(1, len(factorials)):
    factorials[i] = factorials[i-1] * i % MOD

# Function to calculate modular inverse
def mod_inverse(x):
    return pow(x, MOD - 2, MOD)

def countAnagrams(s: str) -> int:
    words = s.split(' ')
    total_anagrams = 1
    for word in words:
        word_count = [0]*26
        for ch in word:
            word_count[ord(ch) - ord('a')] += 1
        word_permutation = factorials[len(word)]
        for count in word_count:
            word_permutation = word_permutation * mod_inverse(factorials[count]) % MOD
        total_anagrams = total_anagrams * word_permutation % MOD
    return total_anagrams

