class Solution:
    def countPartitions(self, nums, k):
        mod = 10**9 + 7
        n = len(nums)
        dp = [[0]*2001 for _ in range(n+1)]
        dp[0][0] = 1
        prefix = [0]*2001 
        prefix[0] = 1
