        price = sorted(list(set(price)))
        n = len(price)
        dp = [-1 for _ in range(k+1)]
        dp[0] = 0
        for i in range(n):
            for j in range(min(i+1, k), 0, -1):
                dp[j] = max(dp[j], dp[j-1] if j != i+1 else -1, min(dp[j-1], price[i]-price[i-j+1] if j != i+1 else price[i]) if j != 1 else price[i])
        return dp[-1]

