class Solution:
    def cycleLengthQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        parent = [0] * (2**n)

        def find(x):
            if parent[x - 1] != 0:
                parent[x - 1] = find(parent[x - 1])
            return parent[x - 1] or x

        result = []
        for a, b in queries:
            a_root = find(a)
            b_root = find(b)
            cycle_length = 0
            while a_root != b_root:
                if a_root > b_root:
                    a, a_root = parent[a - 1] or a // 2, find(parent[a - 1] or a // 2)
                else:
                    b, b_root = parent[b - 1] or b // 2, find(parent[b - 1] or b // 2)
                cycle_length += 1
            result.append(cycle_length + 1)
            parent[a - 1] = parent[b - 1] = b
        return result

