class Solution:
    def maxJump(self, stones: List[int]) -> int:
        stones.append(10 ** 9 + 7)
        n = len(stones)
        l = max(stones[i + 1] - stones[i] for i in range(n - 1)) # maximum difference between two consecutive stones
        r = stones[-2] + 1 # difference between last and first stone
        while l < r:
            m = (l + r) // 2 # mid point in current bounds
            dp = [stones[0] <= m] # initialize dp with frog can reach first stone itself if and only if position of that stone is less than or equal to m
            for i in range(1, n):
                for j in reversed(range(i)): # check for all stones from last to first
                    if stones[i] - stones[j] > m:
                        break
                    if dp[j]:
                        dp.append(True)
                        break
                else:
                    dp.append(False)
            if any(stones[-1] - stones[i] <= m for i in range(n - 1) if dp[i]): # check if frog can return from last stone to first stone in m jumps
                r = m # update right bound
            else:
                l = m + 1 # update left bound
        return l

