from typing import List

class Solution:
    def magnificentSets(self, n: int, edges: List[List[int]]) -> int:
        # build the graph
        g = [[] for _ in range(n+1)]
        for u, v in edges:
            g[u].append(v)
            g[v].append(u)

        # initialize groups and visited status
        group = [0] * (n + 1)
        visited = [False] * (n + 1)

        def dfs(u, grp):
            if visited[u]:
                if group[u] != grp:
                    return False
                return True
            visited[u] = True
            group[u] = grp
            for v in g[u]:
                if not dfs(v, grp ^ 1):
                    return False
            return True

        maxGroup = 0
        for i in range(1, n + 1):
            if not visited[i]:
                if not dfs(i, 0):
                    return -1
                maxGroup = max(maxGroup, max(group))
        return maxGroup + 1

