from heapq import heappop, heappush

class Solution:
    def minScore(self, n: int, roads: [[[int]]]) -> int:
        neighbors = [[] for _ in range(n+1)]
        for u, v, w in roads:
            neighbors[u].append((v, w))
            neighbors[v].append((u, w))
        visited = [False] * (n + 1)
        maxDists = [float('inf')] * (n + 1)
        queue = [(0, 1)]
        maxDists[1] = 0
        while queue:
            maxDist, node = heappop(queue)
            if visited[node]: 
                continue
            visited[node] = True
            for neighbor, distance in neighbors[node]:
                if max(maxDist, distance) < maxDists[neighbor]:
                    maxDists[neighbor] = max(maxDist, distance)
                    heappush(queue, (maxDists[neighbor], neighbor))
        return maxDists[n]

