class Solution:
    def dividePlayers(self, skill):
        skill.sort()
        N = len(skill)
        T = sum(skill) // (N // 2)
        if sum(skill) % (N // 2) != 0:
            return -1
        chemistry = 0
        l, r = 0, N - 1
        while l < r:
            if skill[l] + skill[r] != T:
                return -1
            chemistry += skill[l] * skill[r]
            l += 1
            r -= 1
        return chemistry

