from collections import defaultdict
from heapq import heappop, heappush

class Solution:
    def minimumFuelCost(self, roads, seats):
        graph = defaultdict(list)
        for u, v in roads:
            graph[u].append(v)
            graph[v].append(u)

        distances = []
        stack = [(0,0)]
        visited = set()

        while stack:
            node, dist = stack.pop()
            visited.add(node)
            for neigh in graph[node]:
                if neigh not in visited:
