from collections import defaultdict
from heapq import heappop, heappush

class Solution:
    def minimumFuelCost(self, roads, seats):
        graph = defaultdict(list)
        for u, v in roads:
            graph[u].append(v)
            graph[v].append(u)

        distances = []
        stack = [(0,0)]
        visited = set()

        while stack:
            node, dist = stack.pop()
            visited.add(node)
            for neigh in graph[node]:
                if neigh not in visited:
                    distances.append(dist+1)
                    stack.append((neigh, dist+1))

        distances.sort(reverse=True)
        fuel = 0
        while distances:
            distance = distances.pop(0)
            fuel += distance
            for _ in range(seats-1):
                if distances and distances[0] == distance:
                    distances.pop(0)

        return fuel
