class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

class Solution:
    def minimumOperations(self, root):
        levelNodes = defaultdict(list)

        def BFS(root):
            q = deque([(root, 0)])
            while q:
                node, level = q.popleft()
                if node:
                    levelNodes[level].append(node.val)
                    q.extend([(node.left, level + 1), (node.right, level + 1)])

        def minSwaps(nums):
            temp = sorted([(val, i) for i, val in enumerate(nums)])
            swaps = 0
            visited = {i: False for i in range(len(nums))}

            for i in range(len(nums)):
                if visited[i] or temp[i][1] == i:
                    continue
                cycle_size = 0
                j = i
                while not visited[j]:
                    visited[j] = True
                    j = temp[j][1]
                    cycle_size += 1
                if cycle_size > 0:
                    swaps += cycle_size - 1
            return swaps

        BFS(root)
        return sum(minSwaps(level) for level in levelNodes.values())

