class Solution:
    def destroyTargets(self, nums, space):
        nums.sort(key=lambda x: x%space)  # sort numbers by remainder when divided by space
        current_sequence_max, start, count, res = -1, 0, 0, [0] * len(nums)
        for i, num in enumerate(nums):
            while nums[start] < num - space:  # clean up residues from previous sequences
                if res[start] > count: 
                    count = res[start] 
                start += 1
            res[i] = count + 1  # update count for current sequence
            if num > current_sequence_max:  # update sequence maximum
                current_sequence_max = num
        return min(nums[i] for i in range(len(nums)) if res[i] == max(res))  # return minimum seed with maximum count

