# Definition for a binary tree node.
class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

class Solution:
    def treeQueries(self, root: Optional[TreeNode], queries: List[int]) -> List[int]:
        def height(node):
            if not node:
                return 0
            return 1 + max(height(node.left), height(node.right))

        # Creating node to parent mapping
        node_to_parent = {}
        def dfs(node, parent=None):
            if not node:
                return
            node_to_parent[node.val] = node, parent
            dfs(node.left, node)
            dfs(node.right, node)
        dfs(root)

        # Processing each query
        answer = []
        for query in queries:
            node, parent = node_to_parent[query]
            # Keeping reference if it's a left or right child
            if parent and parent.left == node:
                parent.left = None
            elif parent:
                parent.right = None

            answer.append(height(root))

            # Restoring the tree
            if parent:
                if not parent.left:
                    parent.left = node
                else:
                    parent.right = node
        return answer

