class Solution:
    def mostPopularCreator(self, creators: List[str], ids: List[str], views: List[int]) -> List[List[str]]:
        from collections import defaultdict
        from typing import List
        creator_popularity = defaultdict(lambda: [0, defaultdict(int)])

        for c, i, v in zip(creators, ids, views):
            creator_popularity[c][0] += v
            creator_popularity[c][1][i] = v

        max_popularity = max([x[0] for x in creator_popularity.values()])

        result = []

        for creator, (popularity, videos) in creator_popularity.items():
            if popularity == max_popularity:
                max_views = max(videos.values())
                most_viewed_videos = [i for i, v in videos.items() if v == max_views]
                result.append([creator, min(most_viewed_videos)])

        return result

